import React from "react";
import {useParams} from "react-router-dom";
import {useLocation} from 'react-router-dom';


function User(match){
    // useParams
    const {fname} = useParams();
    console.log(fname);

    // UseLocation
    const location = useLocation();
    console.log(location);

    return(
        <>
        <h3>
            Hi! I am  user {fname}
        </h3>
        <h5>
            My location is : `{location}`;
        </h5>
        </>

    )
}

export default User;
