import React from 'react';
import Food from './Food';
import arr from './ArrFood';
import { useState } from 'react';


const Appcpy = () => {

    const [curr,Ccurr] = useState(arr);

    const clicked=(e)=>{
        const name = e.target.name;
        Ccurr(
            arr.filter((element,id)=>{
                return element.name === name;
            })
        )
    }
  return (
    <>
    <div className='w-4/5 m-auto'>
        <div className='w-4/5 flex justify-between m-auto mb-7'>
           <button onClick={clicked} className='border-0 rounded-lg hover:bg-emerald-200  bg-emerald-100 px-5 py-1' name='all'>All</button>
           <button onClick={clicked} className='border-0 rounded-lg hover:bg-emerald-200  bg-emerald-100 px-5 py-1' name='breakfast' >Breakfast</button>
           <button onClick={clicked} className='border-0 rounded-lg hover:bg-emerald-200  bg-emerald-100 px-5 py-1' name='lunch' >Lunch</button>
           <button onClick={clicked} className='border-0 rounded-lg hover:bg-emerald-200  bg-emerald-100 px-5 py-1' name='dinner' >Dinner</button>
        </div>
    <div className='flex flex-wrap '>
        {
            curr.map((value,index)=>{
                return <Food
                key={value.key}
                heading= {value.heading}
                desc= {value.desc}
                price= {value.price}
                src={value.src}
                />;
            })
        }

    </div>
    </div>
    
    </>
  )
}

export default Appcpy
