import React from "react";
import { useEffect } from "react";
import { useState } from "react";

function Pokemon(){

    const [val,Cnum] = useState({
        num:1,
        name:'',
        moves:0
    });
    
    const cng=(e)=>{
        Cnum(e.target.value);
    }

    const getdata = async()=>{
        const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${val.num}`);
        const data = await res.json();
        console.log(data);
        Cnum({
            moves:data.moves.length(),
            name:data.name
        })
    }
    useEffect(
        ()=>{
            getdata();
        }
    )
    return (
        <>
        <h1>You have selected <span style={{color:'red'}}>{val.num}</span></h1>
        <h1>Pokemon is  <span style={{color:'red'}}>{val.name}</span></h1>
        <h1>Pokemon is  <span style={{color:'red'}}>{val.moves}</span></h1>
        <select value={val.num} onChange={cng}>
        <option value="1">1</option>
        <option value="3">3</option>
        <option value="25">25</option>
        <option value="5">5</option>
        </select>
        </>
    )
}
export default Pokemon;
