const clr = [
    {
        backgroundColor: 'green',
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    },
    {
        backgroundColor: 'yellow',
        padding: '12px 20px',
        cursor: 'pointer',
        border: '3px solid rgb(194, 7, 7)'
    }
]

export default clr;
