import './index.css';

function Forms(){
    const inputEvent =(event)=>{
        console.log(event.target.value);
    }

    return(
        <>
        <div style={{display:'flex' , flexDirection:'column', width:'40%',alignItems:'center', justifyContent:'center'}}> 
    <h1 className="NetHeading">Hello </h1>
    <input type="text" placeholder="Enter text" value=''/>
    <button className="eventButton" style={{margin:'3px'}} onChange={inputEvent} >Submit</button>
    </div>
        </>
    )
}

export default Forms;
