import React from 'react'
import { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Todo from './Todo';

export const Appcpy = () => {
    const [todo, Ctodo] = useState();
    const [list,Clist] = useState([]);
    const [boolEdit,CbEdit] = useState(false);
    const [ID,GID] = useState();

    const inputE = (e) => {
        Ctodo(e.target.value)
    }

    const add=()=>{
        Clist((prevArr)=>{
            return[...prevArr,todo];
        })
        Ctodo('');
    }
    const Dtodo=(id)=>{
        Clist((prevArr)=>{
            return prevArr.filter((value,index)=>{
                return (id!==index);
            })
        })
    }

    const ClearAll=()=>{
        Clist([]);
    }

    const Edit=(id)=>{
        Ctodo(list[id]);
        CbEdit(true);
        GID(ID);
    }
    const Eadd=()=>{
        list[ID] = todo;
        CbEdit(false);
    }
    return (
        <>
            <div>
                <div className='w-1/3 m-auto'>
                    <input
                    className='w-4/6'
                        type="text"
                        placeholder='Add Items...'
                        value={todo}
                        onChange={inputE}
                    />
                    {todo?
                    <button onClick={ boolEdit?Eadd:add } className='hover:text-blue-500'>
                        <AddIcon />
                    </button>:null}
                </div>

                {/* display todo */}
                <div className='w-1/3 m-auto'>
                    {list.map((value,index)=>{
                        return(
                            <Todo
                            key={index}
                            id={index}
                            desc = {value}
                            del={Dtodo}
                            edit = {Edit}
                            />
                        )
                    })}
                </div>
                
                {
                    list.length?                <div className='w-1/3 m-auto'>
                    <button onClick={ClearAll}
                    className='p-2 bg-slate-300 rounded-full hover:bg-slate-200'
                    >Clear All</button>
                </div>:null
                }

            </div>
        </>
    )
}
export default Appcpy;
