import React from 'react';
// import Color from './Color';

function Events(){

    const clr = [
        {
            backgroundColor: 'green',
            height: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
        },
        {
            backgroundColor: 'yellow',
            padding: '12px 20px',
            cursor: 'pointer',
            border: '3px solid rgb(194, 7, 7)'
        }
    ]

    const change =()=>{
        console.log('Hello');
        clr[0].backgroundColor = 'pink';
        clr[1].backgroundColor = 'brown';
    }

    return(
        <>
        <div style={clr[0]}>
            <button style={clr[1]} onClick={change}>Click</button>
        </div>
        </>
    )
}

export default Events;
