import React from 'react'
import { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Todo from './Todo';

export const Appcpy = () => {
    const [todo, Ctodo] = useState();
    const [list,Clist] = useState([]);

    const inputE = (e) => {
        Ctodo(e.target.value)
    }

    const add=()=>{
        Clist((prevArr)=>{
            return[...prevArr,todo];
        })
        Ctodo('');
    }
    const Dtodo=(id)=>{
        Clist((prevArr)=>{
            return prevArr.filter((value,index)=>{
                return (id!==index);
            })
        })
    }

    const ClearAll=()=>{
        
    }
    return (
        <>
            <div>
                <div className='w-1/3 m-auto'>
                    <input
                    className='w-4/6'
                        type="text"
                        placeholder='Add Items...'
                        value={todo}
                        onChange={inputE}
                    />
                    {todo?
                    <button onClick={add} className='hover:text-blue-500'>
                        <AddIcon />
                    </button>:null}
                </div>

                {/* display todo */}
                <div className='w-1/3 m-auto'>
                    {list.map((value,index)=>{
                        return(
                            <Todo
                            key={index}
                            id={index}
                            desc = {value}
                            del={Dtodo}
                            />
                        )
                    })}
                </div>
                <div>
                    <button onClick={ClearAll}>Clear All</button>
                </div>
            </div>
        </>
    )
}
export default Appcpy;
