import React from 'react';
import { useState } from 'react';
import './index.css'


function Events(){

    const green = '#9d80ff';
    var [bg,ChangeBg] = useState(green);

    const change =()=>{
        console.log('Hello');
        const cColor = '#87de100';
        ChangeBg(bg = cColor);
    }

    return(
        <>
        <div className='eventBack' style={{backgroundColor:'bg'}}>
            <button className='eventButton' onClick={change}>Click</button>
        </div>
        </>
    )
}

export default Events;
