import React from 'react';
import { useState } from 'react';
import './index.css'


function Events(){

    const green = '#9d80ff';
    var [bg,ChangeBg] = useState(green);
    const [name,changeName] = useState('Click Me');

    const change =()=>{
        console.log('Hello');
        var cColor = 'red';
        ChangeBg(cColor);
        changeName('Ochh! ðŸ¥°')
    }

    return(
        <>
        <div className='eventBack' style={{backgroundColor: bg}}>
            <button className='eventButton' onClick={change}>{name}</button>
        </div>
        </>
    )
}

export default Events;
