import React from 'react';
import ListTD from './ListTD';
import { useState } from 'react';

function ListMainTD(){
    var [arr,cngarr] = useState([]);
    var [text,ChangeText] = useState('');

    const inputText=(e)=>{
        ChangeText(e.target.value);
    }

    const add=()=>{
       cngarr((prevArr)=>{
        return [...prevArr,text];
       })
       ChangeText('');
    }
    const onDelete=(e)=>{
        console.log(e);
    }
    return (
        <>
        <div className="frm">
            <input 
            type="text"
            placeholder='Enter List'
            onChange={inputText}
            value={text}
            />
            <span><button
            onClick={add}
            >Add</button></span>
        </div>
       { arr.map((value,index)=>{
        return(
            <ListTD 
            key={index}
            // id={index}
            desc ={value} 
            delete = {onDelete}
            />
        )
       })}
        </>
    )
}
export default ListMainTD;
