import React from "react";
import KeepFormat from "./KeepFormat";
import { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';



function KeepMain() {

    const [note, Cngnote] = useState([{
        title: '',
        desc: ''
    }])

    const [currval, Cngcurrval] = useState({
        title: '',
        desc: ''
    })

    const OnCng = (e) => {

        const { name, value } = e.target;

        Cngcurrval((prevVal) => {
            return {
                ...prevVal,
                [name]: value
            }
        })
        console.log(e.target.value);
    }

    const add = (e) => {
        e.preventDefault();

        Cngnote({
            title: currval.title,
            desc: currval.desc
        })
        currval.title = '';
        currval.desc = '';
    }

    return (
        <>
            <div style={{ backgroundColor: 'lightseagreen', padding: '10px 50px' }}>
                <span style={{ fontWeight: 'bolder', fontSize: '30px' }}>Take Note</span>
            </div >
            {/* <div className="write" style={{ display: 'flex', justifyContent: 'center', margin: '8px' }}>
                <input type="text" placeholder="Add new note" style={{ width: '50%', height: '50px' }} />
            </div> */}


            <div>
                <div style={{ display: 'flex', justifyContent: 'center' }}>
                    <div style={{ width: '50%', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
                        <form>
                            <input type="text"
                                placeholder="Title"
                                style={{ width: '100%', height: '40px' }}
                                onChange={OnCng}
                                value={currval.title}
                            />
                            <textarea
                                name="textarea"
                                style={{width:'100%'}}
                                placeholder="Write a note"
                                id="" cols="70" rows="10"
                                onChange={OnCng}
                                value={currval.desc}

                            ></textarea>
                            <div style={{display:'flex',justifyContent:'end', marginTop:'-40px'}}>
                            <Button style={{color:'royalblue',backgroundColor:'lightcyan'}} onClick={add}><AddIcon /></Button>
                            </div>
                            
                        </form>
                    </div>
                </div>
            </div>


            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '5px' }}>
                <div className="show" style={{ width: '30%' }}>
                    {note.map((value, index) => {
                        return (
                            <KeepFormat
                                key={index}
                                id={index}
                                title={value.title}
                                desc={value.desc}
                            />
                        )
                    })}
                </div>
            </div>
        </>
    )
}

export default KeepMain;
